const taskInput = document.getElementById('taskInput');
const addTaskBtn = document.getElementById('addTaskBtn');
const taskList = document.getElementById('taskList');
const totalTasksEl = document.getElementById('totalTasks');
const completedTasksEl = document.getElementById('completedTasks');

let tasks = [];

function renderTasks() {
  taskList.innerHTML = '';

  tasks.forEach((task, index) => {
    const li = document.createElement('li');
    li.className = `task-item ${task.completed ? 'completed' : ''}`;

    const taskText = document.createElement('span');
    taskText.className = 'task-text';
    taskText.textContent = task.text;
    taskText.onclick = () => toggleTask(index);

    const deleteBtn = document.createElement('button');
    deleteBtn.className = 'delete-btn';
    deleteBtn.textContent = 'üóëÔ∏è';
    deleteBtn.onclick = () => deleteTask(index);

    li.appendChild(taskText);
    li.appendChild(deleteBtn);
    taskList.appendChild(li);
  });

  updateStats();
}

function addTask() {
  const text = taskInput.value.trim();

  if (text === '') {
    alert('Por favor escribe una tarea');
    return;
  }

  tasks.push({ text, completed: false });
  taskInput.value = '';
  renderTasks();
  console.log('Tarea a√±adida:', text);
}

function toggleTask(index) {
  tasks[index].completed = !tasks[index].completed;
  renderTasks();
  console.log('Tarea marcada:', tasks[index]);
}

function deleteTask(index) {
  console.log('Tarea eliminada:', tasks[index]);
  tasks.splice(index, 1);
  renderTasks();
}

function updateStats() {
  totalTasksEl.textContent = tasks.length;
  completedTasksEl.textContent = tasks.filter(t => t.completed).length;
}

addTaskBtn.addEventListener('click', addTask);
taskInput.addEventListener('keypress', (e) => {
  if (e.key === 'Enter') addTask();
});

console.log('App de tareas iniciada');

