const animateBtn = document.getElementById('animateBtn');
const animatedBox = document.getElementById('animatedBox');

let animationState = 0;

animateBtn.addEventListener('click', () => {
  // Remover clases anteriores
  animatedBox.className = 'box';

  // Ciclar entre diferentes animaciones
  switch(animationState % 4) {
    case 0:
      animatedBox.style.transform = 'translateX(200px) scale(1.5)';
      animatedBox.style.background = 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)';
      animatedBox.textContent = '‚Üí';
      break;
    case 1:
      animatedBox.style.transform = 'translateY(-50px) rotate(180deg)';
      animatedBox.style.background = 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)';
      animatedBox.textContent = '‚Üë';
      break;
    case 2:
      animatedBox.style.transform = 'translateX(-200px) scale(0.5)';
      animatedBox.style.background = 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)';
      animatedBox.textContent = '‚Üê';
      break;
    case 3:
      animatedBox.style.transform = 'translateY(50px) rotate(360deg)';
      animatedBox.style.background = 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)';
      animatedBox.textContent = '‚Üì';
      break;
  }

  animationState++;
  console.log('Animaci√≥n ejecutada:', animationState);
});

// Log inicial
console.log('Demo de animaciones cargada');
console.log('Haz hover sobre las cajas para ver los efectos');
