// State
let count = 0;
let totalClicks = 0;
let maxValue = 0;
let minValue = 0;

// Elements
const counterDisplay = document.getElementById('counter');
const incrementBtn = document.getElementById('incrementBtn');
const decrementBtn = document.getElementById('decrementBtn');
const resetBtn = document.getElementById('resetBtn');
const stepInput = document.getElementById('stepValue');
const totalClicksEl = document.getElementById('totalClicks');
const maxValueEl = document.getElementById('maxValue');
const minValueEl = document.getElementById('minValue');

// Update display
function updateDisplay() {
  counterDisplay.textContent = count;
  counterDisplay.classList.add('pulse');

  // Color coding
  counterDisplay.classList.remove('positive', 'negative');
  if (count > 0) {
    counterDisplay.classList.add('positive');
  } else if (count < 0) {
    counterDisplay.classList.add('negative');
  }

  setTimeout(() => {
    counterDisplay.classList.remove('pulse');
  }, 300);

  // Update stats
  totalClicksEl.textContent = totalClicks;
  maxValueEl.textContent = maxValue;
  minValueEl.textContent = minValue;

  console.log(`Count: ${count} | Clicks: ${totalClicks} | Max: ${maxValue} | Min: ${minValue}`);
}

// Increment
incrementBtn.addEventListener('click', () => {
  const step = parseInt(stepInput.value) || 1;
  count += step;
  totalClicks++;

  if (count > maxValue) {
    maxValue = count;
  }

  updateDisplay();
});

// Decrement
decrementBtn.addEventListener('click', () => {
  const step = parseInt(stepInput.value) || 1;
  count -= step;
  totalClicks++;

  if (count < minValue) {
    minValue = count;
  }

  updateDisplay();
});

// Reset
resetBtn.addEventListener('click', () => {
  if (confirm('Reset all values?')) {
    count = 0;
    totalClicks = 0;
    maxValue = 0;
    minValue = 0;
    updateDisplay();
    console.log('Counter reset!');
  }
});

// Keyboard shortcuts
document.addEventListener('keydown', (e) => {
  if (e.key === 'ArrowUp') {
    incrementBtn.click();
  } else if (e.key === 'ArrowDown') {
    decrementBtn.click();
  } else if (e.key === 'r' || e.key === 'R') {
    resetBtn.click();
  }
});

console.log('Counter Pro initialized! Use ‚Üë/‚Üì arrows or R to reset.');

