js:
// State
let tasks = JSON.parse(localStorage.getItem('tasks')) || [];
let currentFilter = 'all';

// Elements
const taskInput = document.getElementById('taskInput');
const addBtn = document.getElementById('addBtn');
const taskList = document.getElementById('taskList');
const filterBtns = document.querySelectorAll('.filter-btn');
const clearCompletedBtn = document.getElementById('clearCompleted');
const totalTasksEl = document.getElementById('totalTasks');
const activeTasksEl = document.getElementById('activeTasks');
const completedTasksEl = document.getElementById('completedTasks');

// Initialize
function init() {
  renderTasks();
  updateStats();
  console.log('TaskMaster initialized with', tasks.length, 'tasks');
}

// Add task
function addTask() {
  const text = taskInput.value.trim();

  if (!text) {
    alert('Please enter a task!');
    return;
  }

  const task = {
    id: Date.now(),
    text: text,
    completed: false,
    priority: 'medium',
    createdAt: new Date().toISOString()
  };

  tasks.unshift(task);
  saveTasks();
  taskInput.value = '';
  renderTasks();
  updateStats();

  console.log('Task added:', task);
}

// Toggle task
function toggleTask(id) {
  const task = tasks.find(t => t.id === id);
  if (task) {
    task.completed = !task.completed;
    saveTasks();
    renderTasks();
    updateStats();
    console.log('Task toggled:', task);
  }
}

// Delete task
function deleteTask(id) {
  tasks = tasks.filter(t => t.id !== id);
  saveTasks();
  renderTasks();
  updateStats();
  console.log('Task deleted:', id);
}

// Clear completed
function clearCompleted() {
  const completedCount = tasks.filter(t => t.completed).length;
  if (completedCount === 0) {
    alert('No completed tasks to clear!');
    return;
  }

  if (confirm(`Clear ${completedCount} completed task(s)?`)) {
    tasks = tasks.filter(t => !t.completed);
    saveTasks();
    renderTasks();
    updateStats();
    console.log('Completed tasks cleared');
  }
}

// Filter tasks
function setFilter(filter) {
  currentFilter = filter;
  filterBtns.forEach(btn => {
    btn.classList.toggle('active', btn.dataset.filter === filter);
  });
  renderTasks();
}

// Get filtered tasks
function getFilteredTasks() {
  switch(currentFilter) {
    case 'active':
      return tasks.filter(t => !t.completed);
    case 'completed':
      return tasks.filter(t => t.completed);
    default:
      return tasks;
  }
}

// Render tasks
function renderTasks() {
  const filteredTasks = getFilteredTasks();

  if (filteredTasks.length === 0) {
    taskList.innerHTML = `
      <div class="empty-state">
        <div class="empty-state-icon">üì≠</div>
        <div class="empty-state-text">No tasks yet. Add one to get started!</div>
      </div>
    `;
    return;
  }

  taskList.innerHTML = filteredTasks.map(task => `
    <li class="task-item" data-id="${task.id}">
      <div class="task-checkbox ${task.completed ? 'checked' : ''}" onclick="toggleTask(${task.id})"></div>
      <span class="task-text ${task.completed ? 'completed' : ''}" onclick="toggleTask(${task.id})">
        ${task.text}
      </span>
      <span class="task-priority priority-${task.priority}">${task.priority}</span>
      <button class="task-delete" onclick="deleteTask(${task.id})">üóëÔ∏è</button>
    </li>
  `).join('');
}

// Update stats
function updateStats() {
  const total = tasks.length;
  const completed = tasks.filter(t => t.completed).length;
  const active = total - completed;

  totalTasksEl.textContent = total;
  activeTasksEl.textContent = active;
  completedTasksEl.textContent = completed;
}

// Save to localStorage
function saveTasks() {
  localStorage.setItem('tasks', JSON.stringify(tasks));
}

// Event listeners
addBtn.addEventListener('click', addTask);
taskInput.addEventListener('keypress', (e) => {
  if (e.key === 'Enter') addTask();
});

filterBtns.forEach(btn => {
  btn.addEventListener('click', () => setFilter(btn.dataset.filter));
});

clearCompletedBtn.addEventListener('click', clearCompleted);

// Initialize app
init();

