  padding: 0.25rem 0.75rem;
  background: #334155;
  color: #cbd5e1;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 600;
}

.card-stats {
  display: flex;
  justify-content: space-between;
  padding-top: 1rem;
  border-top: 1px solid #334155;
}

.stat {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #94a3b8;
  font-size: 0.875rem;
}

.stat-icon {
  font-size: 1.125rem;
}

@media (max-width: 768px) {
  .card-grid {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }

  .gallery-header h1 {
    font-size: 2rem;
  }
}

